        let defaultClass = this.arr.find(item => item.id == id)
        if (defaultClass) defaultClass = JSON.stringify({
            name: defaultClass.class_name[0],
            url: defaultClass.class_url[0]
        })
        else throw new Error('cannot find id: ' + id + ' in classTab: ' + this.sign)
        return JSON.parse(getVar(this.sign + id, defaultClass))
    },
    setUrl(url) {
        return url.replace(/\$\{([^}]*)\}/g, (_, id) => this.getClass(id).url)
    }
}
