                        res.url = res.url.replace('.js:', '.js:\nif(MY_RULE)Object.assign(MY_RULE,' + $.stringify({ pages: MY__RULE.pages, pageList: MY__RULE.pageList, find_rule: '', params: '' }) + ');MY_URL="' + MY_URL + '";')
                    } else if (MY_RULE.detail_find_rule) {
