        MY_RULE.last_chapter_rule = RULE.last_chapter_rule
        MY_RULE.params = {}
        MY_RULE.url = MY_URL
        MY_RULE.urlHeaders = URLS[index].options.headers

        // æ­£æ–‡è§£æ
        eval(MY_RULE.preRule)
        if (MY_RULE.find_rule.startsWith('js:')) {
            function getUrl() {
                return MY_URL
            }

            function getResCode() {
                return indexHtml
            }

            setResult = function(el) {
                if (Array.isArray(el.data)) el = el.data
                Array.prototype.push.apply(myCollection_el, el.map((v) => {
                    let MY__RULE = Object.assign({}, MY_RULE, { title: RULE.title })
                    v.desc = RULE.title + ' â–ª ' + (v.desc || '')

                    if (!v.url || ['rule', 'pics', 'toast', 'input', 'copy', 'editFile', 'x5', 'x5WebView', 'x5Play', 'web', 'select', 'x5Rule', 'webRule'].find((vv) => v.url.startsWith(vv + '://'))) {
                        return v
                    } else if (v.url.includes('@rule=')) {
                        let [_, url, rule] = v.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
                        v.url = url
                        MY__RULE.detail_find_rule = rule
                        MY__RULE.detail_col_type = MY_RULE.col_type
                    } else if (v.url.startsWith('hiker://page/')) {
                        if (v.url.includes('rule=') || (v.extra || {}).rule)
                            return v
                        let [_, path, params] = v.url.split('#')[0].match(/^hiker:\/\/page\/(.+?)(?:\?(.*))?$/),
                            subPage = MY_RULE.pageList.find((v) => v.path == path),
                            subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
                        v.url = subUrl ? subUrl.slice(4).replace(/ï¼Ÿï¼Ÿ/g, '?').replace(/ï¼†ï¼†/g, '&') : (v.extra || {}).url || 'hiker://empty?' + (params || '')
                        MY__RULE.detail_find_rule = subPage.rule
                        MY__RULE.detail_col_type = subPage.col_type
                        MY__RULE.params = v.extra || {}
                    } else if (v.url.includes('@lazyRule=')) {
                        v.url = v.url.replace('.js:', '.js:\nif(MY_RULE)Object.assign(MY_RULE,' + $.stringify({ pages: MY_RULE.pages, pageList: MY_RULE.pageList, find_rule: '', params: '' }) + ');MY_URL="' + MY_URL + '";')
                        return v
                    } else if (!MY_RULE.detail_find_rule || v.url.startsWith('hiker://')) { return v }
                    v.extra = { url: v.url, RULE: MY__RULE, pageTitle: v.title }
