            setSearchResult = setResult

            try {
                eval(MY_RULE.find_rule.slice(3))
            } catch (e) {}
        } else {
            let findRule = MY_RULE.find_rule.split(';')
            parseDomForArray(indexHtml, findRule.shift()).forEach((data) => {
                let [title, url, desc, content, img] = findRule.map((v, i) => {
                        try {
                            if (v == '*') return ''
                            else v = (i == 1 || i == 4) ?
                                parseDom(data, v) :
                                parseDomForHtml(data, v)
                            if (i != 1) v = runCode(v)
                            return v
                        } catch (e) { return '' }
                    }),
                    res = {
                        title: title,
                        url: url,
                        desc: RULE.title + ' â–ª ' + desc,
                        content: content,
                        img: img
                    }
                if (res.url) {
