find_rule:
js:
// å½“æ²¡æœ‰ç‹¬ç«‹å°ç¨‹åºé¦–é¡µæ—¶æ¸…é™¤é¡µé¢ç¼“å­˜
addListener("onClose", $.toString(() => {
    if(parseInt(getMyVar('openedIndex', '0')) === 0){
        JSON.parse(readFile("_ruleHashCache") || "[]").forEach((v) => deleteFile(v))
        deleteFile('_ruleHashCache')
    }
}))
const QING_TITLE = getMyVar('myCollection'),
    newWindow = readFile('newWindow'),
    typeShow = getMyVar('typeShow', '');
let el = [{
        title: ' ' + QING_TITLE.bold() + ' &nbsp &nbsp ' + 'âš™ è½»åˆé›†è®¾ç½® âš™'.small(),
        url: 'hiker://page/Config?rule=' + QING_TITLE + '&#noRecordHistory#',
        img: MY_RULE.icon || 'hiker://images/icon1',
        col_type: 'avatar'
    }, {
        url: '"hiker://search?s="+input',
        desc: 'æœä½ æƒ³è¦çš„...',
        title: 'æœç´¢',
        col_type: 'input',
        extra: {
            rules: $.toString((QING_TITLE, typeShow) => {
                let rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)()
                if (typeShow) rules = rules.filter((v) => v.type == typeShow)
                return JSON.stringify(rules)
            }, QING_TITLE, typeShow),
            defaultValue: getMyVar('searchKey', ''),
            onChange: "putMyVar('searchKey',input)"
        }
    }],
    rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)(),
    types = {'':'å…¨éƒ¨'};
    Array.from(new Set(rules.map(e=>e.type))).forEach(t=>types[t]=t);
for (let k in types){
    if (!k || rules.find((v) => v.type == k)){
	      let count = rules.filter(it => !k || it.type === k).length;
        el.push({
            title: 'â€œâ€œâ€â€' + (k == typeShow ? (types[k]+count).bold().fontcolor('#12b668') : types[k]+count),
            url: k == typeShow ? 'hiker://empty' : $('#noLoading#').lazyRule((k) => {
                putMyVar('typeShow', k)
                refreshPage(false)
                return 'hiker://empty'
            }, k),
            col_type: 'scroll_button'
        })
	}
}
eval(JSON.parse(fetch('hiker://page/urlParse?rule=' + QING_TITLE)).rule)
if (typeShow) rules = rules.filter((v) => v.type == typeShow)
rules.forEach((v) => {
    let urlParam = v.url.split(';'),
        ruleTmp = {
            title: v.title, url: urlParam.shift(),
            class_name: v.class_name, class_url: v.class_url,
            area_name: v.area_name, area_url: v.area_url,
            year_name: v.year_name, year_url: v.year_url,
            sort_name: v.sort_name, sort_url: v.sort_url
        },
        fypage = ruleTmp.url.includes('fypage') ? 'fypage' : '1',
        domain = (/(https?:\/\/.+?)(?:\/|$)/i.exec(ruleTmp.url) || [, 'hiker://empty'])[1],
        d = {
            title: v.title,
            url: 'hiker://page/frameLoad',
            img: v.icon || 'hiker://images/icon1',
            extra: {
                url: domain + '/#noHistory##noRecordHistory####' + JSON.stringify({
                    RULE: base64Encode(JSON.stringify(ruleTmp)),
                    pageNum: fypage,
                    QING_TITLE: getMyVar('myCollection')
                }) + '.js:eval(JSON.parse(fetch("hiker://page/urlParseï¼Ÿï¼Ÿrule=' + QING_TITLE + '")).rule)ï¼›ï¼›indexUrl(input);' + paramHandle(urlParam, v.ua),
                RULE: { title: v.title },
                isIndex: true
            }
        }
    if (newWindow) Object.assign(d.extra, {
        newWindow: true,
        windowId: QING_TITLE + '_' + d.title
    })
    el.push(d)
})
setResult(el)

