        let folded = this.fold && getVar('fold_' + this.sign, '')
        if (this.arr.length > 1 && this.fold) el.push({
            title: 'â€œâ€œâ€â€' + (folded ? 'â–¶' : 'â–¼').fontcolor('#049eff'),
            url: $('#noLoading#').lazyRule((sign, folded) => {
                putVar('fold_' + sign, { '': 'T', 'T': '' } [folded])
                refreshPage(false)
                return 'hiker://empty'
            }, this.sign, folded),
            col_type: 'scroll_button'
        })
        let arr = folded ? [this.arr[0]] : this.arr
        arr.forEach((v) => {
            let { id, class_name, class_url } = v,
            selected = JSON.stringify(this.getClass(id))
            class_name.forEach((name, i) => {
                let url = class_url[i],
                    now = JSON.stringify({ name: name, url: url })
                el.push({
                    title: selected == now ? 'â€œâ€œâ€â€' + name.fontcolor(this.color).bold() : name,
