//log('å…³é—­è‡ªå®šä¹‰åˆ†ç±»ï¼Ÿ'+disTabModify);
const {color,small,unique2} = $.require("hiker://page/tools?rule="+ QING_TITLE);

let el = [{
        title: ' ' + QING_TITLE.bold() + ' &nbsp &nbsp ' + 'âš™ è½»åˆé›†è®¾ç½® âš™'.small(),
        url: 'hiker://page/Config?rule=' + QING_TITLE + '&#noRecordHistory#',
