var res = {};
var d=[];
var page=MY_URL.split('#')[2];
var urls=[];

for(var i=0;i<16;i++){
  urls.push({
	url:MY_URL.split('#')[1].slice(0,-5)+'_'+(page*16+i-15)+'.html',
	options:{
	  headers:{"User-Agent":MOBILE_UA}
	}
  });
}

