    let iurl=zsj.images[0];
    d.push({
      url:iurl,
      pic_url:iurl,
      col_type:'pic_1_full'
    });
  });
}
catch(e){
    d.push({
      title:"over",
	   col_type:'text_1'
    });
}
res.data=d;
setHomeResult(res);
